//realAngle = Math.max(Math.min(realAngle,225.0),0.0);

if (window.birdbrain.servos[realPort] === undefined || Date.now() > window.birdbrain.servos.lastRequestSent + 1000) {
    window.birdbrain.servos.setServoAngle(realPort, position);
}

window.birdbrain.servos[realPort] = position;

